mapper.select:
select s.id,s.name,t.name typeName,s.address,s.phone
from supplier s left join `type` t on s.typeId=t.id
where s.id=#{id}

mapper.update:
update supplier set name =#{name},
                    typeId=(select id from `type` where name=#{typeName}),
                    address=#{address},
                    phone=#{phone}
where  id=#{id}
